mapper.select:
select sd.id,sd.platform_id,sd.year,sd.month,sd.day,sd.count
,usdr.create_times
from sign_date sd

    left join user_sign_date_relation usdr on sd.id = usdr.sign_date_id


    left join user u on usdr.uid = u.id

where 1=1

    and usdr.uid = #{uid,jdbcType=INTEGER}


    and u.username = #{username,jdbcType=VARCHAR}


    and sd.year = #{year,jdbcType=INTEGER}
    and sd.month = #{month,jdbcType=INTEGER}

order by sd.day desc;

select us.id,us.uid,us.score,us.retroactive_count,us.succession_count, u.username
from user_score us
left join user u on us.uid = u.id
where 1=1
and u.username=#{username,jdbcType=VARCHAR}
and us.uid = #{uid,jdbcType=INTEGER}

mapper.update:
update user_score left join user u on user_score.uid = u.id
set score = if(score+#{score} < 0,0,score+#{score})
where u.platform_id = #{platformId,jdbcType=INTEGER};
